import instance from '@/utils/request';
import useAuthStore from '@/stores/auth';
// 叫号
export function callNumber({ phone, peopleNumber }) {
  const authStore = useAuthStore();
  const token = authStore.token;
  const params = new URLSearchParams();
  params.append('authenticity', token);
  params.append('phone', phone);
  params.append('peopleNumber', peopleNumber);
  return instance.post('/callingnumber/add', null, {
    params
  });
}

// 移除叫号
export function removeCallingNumber(callingNumberId) {
  const authStore = useAuthStore();
  const token = authStore.token;
  const params = new URLSearchParams();
  params.append('authenticity', token);
  params.append('callingNumberId', callingNumberId);
  return instance.post('/callingnumber/remove', null, {
    params
  });
}

// 获取所有队列
export function getAllQueues() {
  const authStore = useAuthStore();
  const token = authStore.token;
  const params = new URLSearchParams();
  params.append('authenticity', token);
  return instance.get('/callingnumber/queues', {
    params
  });
}
