import instance from '@/utils/request';

export const login = async (credentials) => {
  try {
    const response = await instance.post('/users/login', null, {
      params: credentials
    });
    return response; 
  } catch (error) {
    throw error.response?.data?.message || '登录失败，请稍后再试'
  }
}
