    const __returned__ = {
      addDishForm,
      emit,
      handleCancel,
      handleSubmit,
      ref,
      watch,
      get useMenuStore() {
        return useMenuStore;
      },
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
