import instance from '@/utils/request';
import { useMenuStore } from '@/stores/menu';

export const getMenuList = async () => {
    try {
        // 根据你的API截图，获取菜单列表的GET请求到/menus/all，且不需要参数
        const response = await instance.get('/menus/all');
        return response.data;
    } catch (error) {
        throw error.response?.data?.message || '获取菜单列表失败';
    }
};

export const getHotDishes = async (startTime, endTime) => {
    try {
        const menuStore = useMenuStore();
        const params = {
            authenticity: menuStore.token,
            startTime: startTime,
            endTime: endTime
        };
        const response = await instance.get('/checkout/popular-dishes', { params });
        return response.data;
    } catch (error) {
        throw error.response?.data?.message || '获取热品推荐失败';
    }
};
