import { ref, watch } from 'vue';
import { useMenuStore } from '@/stores/menu';
import { ElMessage } from 'element-plus';
//这里定义一个props接受父组件传来的数据

export default {
  __name: 'MenuAddCard',
  props: {
    dish: {
      type: Object,
      default: () => ({})
    }
  },
  emits: ['add-success', 'close'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;

    //存放watch监听的数据
    const form = ref({
      dishId: '',
      dishName: '',
      price: '',
      description: '',
      imageUrl: ''
    });
    const emit = __emit;
    const handleCancel = () => {
      emit('close');
    };
    const handleSubmit = async () => {
      try {
        const menuStore = useMenuStore();
        await menuStore.addDish(form.value);
        emit('add-success');
      } catch (error) {
        ElMessage.error('添加菜品失败: ' + error.message);
      }
    };

    // 监听props传入的dish对象，初始化表单
    watch(() => props.dish, newVal => {
      if (newVal) {
        form.value = {
          dishId: newVal.dishId || '',
          dishName: newVal.dishName || '',
          price: newVal.price || '',
          description: newVal.description || '',
          imageUrl: newVal.imageUrl || ''
        };
      }
    }, {
      immediate: true,
      deep: true
    });
    const __returned__ = {
      props,
      form,
      emit,
      handleCancel,
      handleSubmit,
      ref,
      watch,
      get useMenuStore() {
        return useMenuStore;
      },
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
