//这个from里面写顾客的Id
//写桌子的Id
//写peopleCount
//利用上面三个参数创建订单
//oderId是刚刚创建订单的时候返回的
//需要一个DishId的input
//需要一个quaitity的input
//像MenuForm一样，在整个页面的左侧展示菜品，双击选择菜品后，在右侧展示菜品Id和数量
//在右侧的Form的div中要有一个table展示对应订单中已经有的菜品信息
//管理员可以根据tableId查询订单，然后根据orderId查询订单中的菜品信息
//管理员可以根据orderId来结账

<template>
  <div class="flex gap-8">
    <!-- 左侧：菜品列表 -->
    <div class="w-1/2">
      <h2 class="text-lg font-bold mb-2">菜品列表（双击添加）</h2>
      <el-table
        :data="menuList"
        style="width: 100%; font-size: 18px;"
        height="600"
        @row-dblclick="handleDishDblClick"
        highlight-current-row>
        <el-table-column prop="dishId" label="菜品ID" width="120" align="center" />
        <el-table-column prop="dishName" label="菜品名称" width="200" align="center" />
        <el-table-column prop="price" label="价格" width="120" align="center">
          <template #default="scope">¥{{ scope.row.price }}</template>
        </el-table-column>
        <el-table-column prop="description" label="描述" min-width="200" />
      </el-table>
    </div>

    <!-- 右侧：订单操作与信息 -->
    <div class="w-1/2">
      <h2 class="text-lg font-bold mb-2">订单操作</h2>
      <el-form label-width="80px" class="mb-4">
        <el-form-item label="顾客ID">
          <el-input v-model="customerId" type="number" placeholder="输入顾客ID" />
        </el-form-item>
        <el-form-item label="桌子ID">
          <el-input v-model="tableId" type="number" placeholder="输入桌子ID" />
        </el-form-item>
        <el-form-item label="人数">
          <el-input v-model="peopleCount" type="number" min="1" placeholder="输入人数" />
        </el-form-item>
        <el-form-item>
          <el-button type="primary" @click="handleCreateOrder" :disabled="orderId">创建订单</el-button>
          <span v-if="orderId" class="ml-4 text-green-600">订单ID: {{ orderId }}</span>
        </el-form-item>
      </el-form>

      <el-divider>已选菜品</el-divider>
      <el-table :data="selectedDishes" style="width: 100%" size="small">
        <el-table-column prop="dishId" label="菜品ID" width="80" align="center" />
        <el-table-column prop="dishName" label="菜品名称" width="120" align="center" />
        <el-table-column prop="quantity" label="数量" width="80" align="center">
          <template #default="scope">
            <el-input-number v-model="scope.row.quantity" :min="1" @change="q => handleUpdateQuantity(scope.row, q)" />
          </template>
        </el-table-column>
        <el-table-column label="操作" width="80" align="center">
          <template #default="scope">
            <el-button size="small" type="danger" @click="removeDish(scope.row)">移除</el-button>
          </template>
        </el-table-column>
      </el-table>

      <el-divider>订单菜品信息</el-divider>
      <el-form label-width="100px" class="mb-2">
        <el-form-item label="根据桌号查订单">
          <el-input v-model="queryTableId" type="number" placeholder="输入桌子ID" style="width: 120px;" />
          <el-button @click="handleQueryOrderByTableId" class="ml-2">查询</el-button>
        </el-form-item>
        <el-form-item label="根据订单查菜品">
          <el-input v-model="queryOrderId" type="number" placeholder="输入订单ID" style="width: 120px;" />
          <el-button @click="handleQueryOrderDetails" class="ml-2">查询</el-button>
        </el-form-item>
      </el-form>
      <el-table :data="orderDetails" style="width: 100%" size="small">
        <el-table-column prop="dishId" label="菜品ID" width="80" align="center" />
        <el-table-column prop="dishName" label="菜品名称" width="120" align="center" />
        <el-table-column prop="quantity" label="数量" width="80" align="center" />
      </el-table>

      <el-divider>结账</el-divider>
      <el-form label-width="80px">
        <el-form-item label="订单ID">
          <el-input v-model="checkoutOrderId" type="number" placeholder="输入订单ID" style="width: 120px;" />
          <el-button type="success" class="ml-2" @click="handleCheckout">结账</el-button>
        </el-form-item>
      </el-form>
      <div v-if="message" class="mt-2 text-green-600">{{ message }}</div>
      <div v-if="error" class="mt-2 text-red-600">{{ error }}</div>
    </div>
  </div>
</template>

<script setup>
import { ref, onMounted } from 'vue';
import { ElMessage } from 'element-plus';
import { getMenuList } from '@/api/menu';
import {
  createOrder,
  addDishToOrder,
  removeDishFromOrder,
  updateDishQuantity,
  getOrderIdWithDishId,
  getOrderDetails,
  checkoutOrder
} from '@/api/checkout';

const customerId = ref('');
const tableId = ref('');
const peopleCount = ref(1);
const orderId = ref(null);
const menuList = ref([]);
const selectedDishes = ref([]); // {dishId, dishName, quantity}
const message = ref('');
const error = ref('');
const orderDetails = ref([]);
const queryTableId = ref('');
const queryOrderId = ref('');
const checkoutOrderId = ref('');

onMounted(async () => {
  await fetchMenuList();
});

async function fetchMenuList() {
  try {
    const res = await getMenuList();
    menuList.value = Array.isArray(res) ? res : (res.data || []);
  } catch (err) {
    error.value = err;
  }
}

async function handleCreateOrder() {
  message.value = '';
  error.value = '';
  try {
    const res = await createOrder({
      tableId: Number(tableId.value),
      customerId: Number(customerId.value),
      peopleCount: Number(peopleCount.value)
    });
    orderId.value = res.data?.orderId || res.data?.id || res.data?.orderID || null;
    if (orderId.value) {
      ElMessage.success('订单创建成功，ID: ' + orderId.value);
    } else {
      ElMessage.warning('订单创建成功，但未返回订单ID');
    }
  } catch (err) {
    error.value = err;
  }
}

function handleDishDblClick(row) {
  if (!orderId.value) {
    ElMessage.warning('请先创建订单');
    return;
  }
  // 检查是否已添加
  const exist = selectedDishes.value.find(d => d.dishId === row.dishId);
  if (exist) {
    exist.quantity++;
    handleUpdateQuantity(exist, exist.quantity);
    return;
  }
  // 添加到订单
  addDishToOrder({ orderId: orderId.value, dishId: row.dishId, quantity: 1 })
    .then(() => {
      selectedDishes.value.push({ ...row, quantity: 1 });
      ElMessage.success('已添加菜品: ' + row.dishName);
    })
    .catch(err => {
      error.value = err;
    });
}

function removeDish(row) {
  if (!orderId.value) return;
  removeDishFromOrder({ orderId: orderId.value, dishId: row.dishId })
    .then(() => {
      selectedDishes.value = selectedDishes.value.filter(d => d.dishId !== row.dishId);
      ElMessage.success('已移除菜品');
    })
    .catch(err => {
      error.value = err;
    });
}

function handleUpdateQuantity(row, quantity) {
  if (!orderId.value) return;
  updateDishQuantity({ orderId: orderId.value, dishId: row.dishId, quantity })
    .then(() => {
      row.quantity = quantity;
      ElMessage.success('数量已更新');
    })
    .catch(err => {
      error.value = err;
    });
}

async function handleQueryOrderByTableId() {
  message.value = '';
  error.value = '';
  try {
    const res = await getOrderIdWithDishId(Number(queryTableId.value));
    // 假设返回 [{orderId, dishId, dishName, quantity}]，可根据实际调整
    orderDetails.value = res.data || [];
  } catch (err) {
    error.value = err;
  }
}

async function handleQueryOrderDetails() {
  message.value = '';
  error.value = '';
  try {
    const res = await getOrderDetails(Number(queryOrderId.value));
    orderDetails.value = res.data || [];
  } catch (err) {
    error.value = err;
  }
}

async function handleCheckout() {
  message.value = '';
  error.value = '';
  try {
    await checkoutOrder(Number(checkoutOrderId.value));
    message.value = '结账成功！';
    ElMessage.success('结账成功！');
  } catch (err) {
    error.value = err;
  }
}
</script>

<style scoped>
.flex { display: flex; }
.gap-8 { gap: 2rem; }
.w-1\/2 { width: 50%; }
.ml-2 { margin-left: 0.5rem; }
.ml-4 { margin-left: 1rem; }
.mt-2 { margin-top: 0.5rem; }
.text-green-600 { color: #16a34a; }
.text-red-600 { color: #dc2626; }
.el-table {
  font-size: 18px;
}
</style>
