// 这个文件里放顾客登录的顾客ID的input框和顾客phone的input框还有登录按钮
//根据获取的顾客信息来判断顾客输入的Id和phone是否匹配
//如果匹配，就跳转到顾客的菜单列表页面
//在Logingorm中加一个按钮，点击它就能打开这个顾客登陆的页面
//需要给之后打开的组件传递当前用户的Id
//如果登陆成功就路由跳转到CustomerDashboard.vue文件

<template>
  <form @submit.prevent="handleCustomerLogin" class="max-w-sm mx-auto mt-10 p-6 bg-white rounded shadow">
    <div class="mb-4">
      <label for="customerId" class="block mb-2">顾客ID</label>
      <input
        id="customerId"
        v-model="form.customerId"
        type="text"
        required
        placeholder="请输入顾客ID"
        class="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <div class="mb-4">
      <label for="phone" class="block mb-2">手机号</label>
      <input
        id="phone"
        v-model="form.phone"
        type="text"
        required
        placeholder="请输入手机号"
        class="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <div v-if="error" class="text-red-500 mb-4">{{ error }}</div>
    <div class="flex justify-center">
      <button type="submit" class="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-md cursor-pointer">登录</button>
    </div>
  </form>
</template>

<script setup>
import { ref } from 'vue'
import { useRouter } from 'vue-router'
import { ElMessage } from 'element-plus'
import { getCustomerList } from '@/api/customer'

const form = ref({ customerId: '', phone: '' })
const error = ref(null)
const router = useRouter()

const handleCustomerLogin = async () => {
  error.value = null
  try {
    const res = await getCustomerList()
    const match = (res.data || res).find(c => String(c.customerId) === String(form.value.customerId) && String(c.phone) === String(form.value.phone))
    if (match) {
      localStorage.setItem('customerId', match.customerId)
      ElMessage.success('登录成功')
      router.push('/customer-dashboard/menu')
    } else {
      error.value = '顾客ID或手机号错误'
    }
  } catch (e) {
    error.value = e.message || '登录失败'
  }
}
</script>
