import { defineStore } from 'pinia';
import { getMenuList, getHotDishes } from '@/api/menu';

export const useMenuStore = defineStore('menu', {
    state: () => ({
        menuList: [],
        hotDishes: [],
        loading: false,
        error: null
    }),
    actions: {
        async fetchMenuList() {
            this.loading = true;
            try {
                const response = await getMenuList();
                this.menuList = response.data;
            } catch (error) {
                this.error = error;
            } finally {
                this.loading = false;
            }
        },
        async fetchHotDishes(startTime, endTime) {
            this.loading = true;
            this.error = null;
            try {
                const response = await getHotDishes(startTime, endTime);
                this.hotDishes = response.data || [];
            } catch (error) {
                this.error = error;
                this.hotDishes = [];
            } finally {
                this.loading = false;
            }
        }
    }
});
