import instance from '@/utils/request';
import useAuthStore from '@/stores/auth';
// 叫号
export async function callNumber({ phone, peopleNumber }) {
  const authStore = useAuthStore();
  const token = authStore.token;
  const params ={
    authenticity: token,
    phone,
    peopleNumber
  }
  const response = await instance.post('/callingnumber/add', null, {
    params
  });
  return response;
}

// 移除叫号
export async function removeCallingNumber(callingNumberId) {
  const authStore = useAuthStore();
  const token = authStore.token;
    const params ={
    authenticity: token,
    callingNumberId
  }
  const response = await instance.post('/callingnumber/remove', null, {
    params
  });
  return response;
}

// 获取所有队列
export async function getAllQueues() {
  const authStore = useAuthStore();
  const token = authStore.token;
  const params ={
    authenticity: token,
  }
  const response = await instance.get('/callingnumber/queues', {
    params
  });
  return response;
}
