//这个组件参考components中的checkout文件夹中的给组件
//但是用户看到的界面是和view文件夹中的checkoutview.vue文件一样的
//但是创建订单的时候，顾客Id是固定的，就是登陆的时候传进来的顾客的Id(订单创建和订单更新的那里)

<template>
  <CheckoutForm :customer-id="customerId" />
</template>
<script setup>
import { ref, onMounted } from 'vue'
import CheckoutForm from '@/components/checkout/CheckoutForm.vue'

const customerId = ref('')
onMounted(() => {
  customerId.value = localStorage.getItem('customerId') || ''
})
</script>
