import instance from '@/utils/request';
import useAuthStore from '@/stores/auth';
// 叫号
export const callNumber = async ({ phone, peopleNumber }) => {
  try{
  const authStore = useAuthStore();
  const token = authStore.token;
  const params ={
    authenticity: token,
    phone,
    peopleNumber
  }
  const response = await instance.post('/callingnumber/add', null, {
    params
    });
    return response;
  } catch (error) {
    throw error.response?.data?.message || '叫号失败';
  }
}

// 移除叫号
export const removeCallingNumber = async (callingNumberId) => {
  try{
  const authStore = useAuthStore();
  const token = authStore.token;
    const params ={
    authenticity: token,
    callingNumberId
  }
  const response = await instance.post('/callingnumber/remove', null, {
    params
  });
  return response;
  } catch (error) {
    throw error.response?.data?.message || '移除叫号失败';
  }
}

// 获取所有队列
export const getAllQueues = async () => {
  try{
  const authStore = useAuthStore();
  const token = authStore.token;
  const params ={
    authenticity: token,
  }
  const response = await instance.get('/callingnumber/queues', {
    params
  });
  return response;
  } catch (error) {
    throw error.response?.data?.message || '获取队列失败';
  }
}
